export function slugToTranslationKey(slug: string): string {
  return slug
    .replace(/-calculator$/, "")
    .replace(/-generator$/, "")
    .replace(/-converter$/, "")
    .replace(/-([a-z])/g, (_, l) => l.toUpperCase());
}

